query GetUserBadges($user: Bytes!) {
  badges {
    badgeId
    uri
    badgeTiers(orderBy: tier, orderDirection: asc) {
      points
      tier
      uri
    }
  }
  accountBadges(where: {user: $user}) {
    points
    tier
    badge {
      badgeId
      uri
      badgeTiers(orderBy: tier, orderDirection: asc) {
        points
        tier
        uri
      }
    }
  }
}
